import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Certifique-se de que está importando corretamente
import { useAuth } from '../../contexts/AuthContext'; // Verifique o caminho do seu contexto
import './LoginPage.css';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login, currentUser } = useAuth(); // Pegando login e currentUser do contexto
  const navigate = useNavigate(); // Hook para navegação
  
  // Se o usuário já estiver logado, redireciona para a página inicial
  if (currentUser) {
    navigate('/'); // Direciona para a página inicial se o usuário já estiver logado
    return null; // Evita que o conteúdo da página de login seja exibido
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);
    
    try {
      await login(formData.email, formData.password); // Realiza o login
      navigate('/'); // Redireciona para a página inicial após o login bem-sucedido
    } catch (err) {
      setError(err.message || 'Falha no login. Verifique suas credenciais.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <img src="/logo.png" alt="Mnemo Logo" className="logo" />
          <h1>Login</h1>
        </div>
        
        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Digite seu email"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Senha</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              minLength="6"
              placeholder="Digite sua senha"
            />
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="spinner"></span> Carregando...
              </>
            ) : (
              'Entrar'
            )}
          </button>
        </form>

        <div className="login-footer">
          <p>Não tem uma conta? <Link to="/register">Crie uma</Link></p>
          <p className="forgot-password">
            <Link to="/forgot-password">Esqueceu sua senha?</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
