// Adicione estas funções ao arquivo existente
export const getRecentDecks = (userId) => {
  const decks = getFromStorage('user_decks') || {};
  return Object.values(decks)
    .filter(deck => deck.userId === userId)
    .sort((a, b) => new Date(b.lastAccessed) - new Date(a.lastAccessed))
    .slice(0, 3);
};

export const getRecommendedDecks = (userId) => {
  // Simulação - na prática viria de uma recomendação por IA
  return [
    {
      id: 'genetics-101',
      name: 'Genética Básica',
      cardCount: 25,
      progress: 40,
      icon: 'fa-dna'
    },
    {
      id: 'neuroscience-101',
      name: 'Neurociência',
      cardCount: 18,
      progress: 15,
      icon: 'fa-brain'
    }
  ];
};
